const inputData = $input.first().json.Segments;
const maxDuration = 2.0; // Set your desired max segment length in seconds

function timeToSeconds(t) {
  const [h, m, s] = t.split(/[:,]/).map(Number);
  return h * 3600 + m * 60 + s + (parseFloat(`0.${t.split(',')[1]}`) || 0);
}

function secondsToTime(sec) {
  const h = String(Math.floor(sec / 3600)).padStart(2, '0');
  const m = String(Math.floor((sec % 3600) / 60)).padStart(2, '0');
  const s = String(Math.floor(sec % 60)).padStart(2, '0');
  const ms = String(Math.round((sec % 1) * 1000)).padStart(3, '0');
  return `${h}:${m}:${s},${ms}`;
}

const output = [];
let currentIndex = 1;

for (const segment of inputData) {
  const startSec = timeToSeconds(segment.start);
  const endSec = timeToSeconds(segment.end);
  const duration = parseFloat((endSec - startSec).toFixed(3));

  if (duration <= maxDuration) {
    output.push({
      index: currentIndex++,
      start: segment.start,
      end: segment.end,
      text: segment.text,
      duration: duration
    });
    continue;
  }

  const words = segment.text.split(/\s+/);
  const numChunks = Math.ceil(duration / maxDuration);
  const wordsPerChunk = Math.ceil(words.length / numChunks);

  let splitStart = startSec;
  let pointer = 0;

  while (pointer < words.length) {
    const chunkWords = words.slice(pointer, pointer + wordsPerChunk);
    const chunkDuration = Math.min(maxDuration, endSec - splitStart);
    const splitEnd = splitStart + chunkDuration;

    output.push({
      index: currentIndex++,
      start: secondsToTime(splitStart),
      end: secondsToTime(splitEnd),
      text: chunkWords.join(' '),
      duration: parseFloat((splitEnd - splitStart).toFixed(3))
    });

    pointer += wordsPerChunk;
    splitStart = splitEnd;
  }
}

return [
  {
    json: {
      segments: output
    }
  }
];

const inputSegments = $input.first().json.segments;

// We will create a new array for merged segments
const mergedSegments = [];

for (let i = 0; i < inputSegments.length; i++) {
  const current = inputSegments[i];

  if (current.duration < 2 && mergedSegments.length > 0) {
    // Merge current segment into the previous segment in mergedSegments

    const prev = mergedSegments[mergedSegments.length - 1];

    // Update prev segment text (add a space between)
    prev.text = prev.text + ' ' + current.text;

    // Update prev segment end time to current segment end time
    prev.end = current.end;

    // Update duration
    const prevStartSec = timeToSeconds(prev.start);
    const newEndSec = timeToSeconds(current.end);
    prev.duration = parseFloat((newEndSec - prevStartSec).toFixed(3));

  } else {
    // No merge needed, just push current segment
    mergedSegments.push({ ...current }); // clone to avoid mutation
  }
}

// Helper function to convert time string to seconds
function timeToSeconds(t) {
  const [h, m, s] = t.split(/[:,]/).map(Number);
  return h * 3600 + m * 60 + s + (parseFloat(`0.${t.split(',')[1]}`) || 0);
}

return [
  {
    json: {
      segments: mergedSegments
    }
  }
];

